{"filter":false,"title":"views.py","tooltip":"/cart/views.py","undoManager":{"mark":1,"position":1,"stack":[[{"start":{"row":0,"column":0},"end":{"row":3,"column":0},"action":"remove","lines":["from django.shortcuts import render","","# Create your views here.",""],"id":2},{"start":{"row":0,"column":0},"end":{"row":33,"column":41},"action":"insert","lines":["from django.shortcuts import render, redirect, reverse","","# Create your views here.","def view_cart(request):","    \"\"\"A View that renders the cart contents page\"\"\"","    return render(request, \"cart.html\")","","","def add_to_cart(request, id):","    \"\"\"Add a quantity of the specified product to the cart\"\"\"","    quantity = int(request.POST.get('quantity'))","","    cart = request.session.get('cart', {})","    cart[id] = cart.get(id, quantity)","","    request.session['cart'] = cart","    return redirect(reverse('index'))","","","def adjust_cart(request, id):","    \"\"\"","    Adjust the quantity of the specified product to the specified","    amount","    \"\"\"","    quantity = int(request.POST.get('quantity'))","    cart = request.session.get('cart', {})","","    if quantity > 0:","        cart[id] = quantity","    else:","        cart.pop(id)","    ","    request.session['cart'] = cart","    return redirect(reverse('view_cart'))"]}],[{"start":{"row":16,"column":4},"end":{"row":16,"column":37},"action":"remove","lines":["return redirect(reverse('index'))"],"id":3},{"start":{"row":16,"column":4},"end":{"row":16,"column":39},"action":"insert","lines":["return render(request, \"cart.html\")"]}]]},"ace":{"folds":[],"scrolltop":291,"scrollleft":0,"selection":{"start":{"row":19,"column":29},"end":{"row":19,"column":29},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":13,"state":"start","mode":"ace/mode/python"}},"timestamp":1596563192790,"hash":"047e7d13b86cdf5222e7a621571f24a2afc37a27"}